import React, { useState } from "react";
import TaskList from "./TaskList";
import TaskForm from "./TaskForm";

const App = () => {
  const [currentTask, setCurrentTask] = useState(null);

  const refreshTasks = () => {
    // Optionally use a function to refresh tasks
    // This can be handled inside TaskList
  };

  return (
    <div>
      <h1>Task Manager</h1>
      <TaskForm currentTask={currentTask} onSubmit={refreshTasks} />
      <TaskList onEdit={setCurrentTask} />
    </div>
  );
};

export default App;
