import React, { useEffect, useState } from 'react';
import { List, ListItem, ListItemText, Typography, Divider, Button } from '@mui/material';
import { getTasks, deleteTask } from './api';

const TaskList = ({ onEdit }) => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const fetchTasks = async () => {
      const data = await getTasks();
      setTasks(data);
    };

    fetchTasks();
  }, []);

  const handleDelete = async (id) => {
    await deleteTask(id);
    setTasks(tasks.filter(task => task.id !== id));
  };

  return (
    <List sx={{ marginTop: 3 }}>
      {tasks.map((task) => (
        <div key={task.id}>
          <ListItem>
            <ListItemText
              primary={<Typography variant="h6">{task.title}</Typography>}
              secondary={<Typography variant="body2">{task.description}</Typography>}
            />
            <Button variant="outlined" onClick={() => onEdit(task)}>
              Edit
            </Button>
            <Button variant="outlined" color="error" onClick={() => handleDelete(task.id)}>
              Delete
            </Button>
          </ListItem>
          <Divider />
        </div>
      ))}
    </List>
  );
};

export default TaskList;
